-- Trigger 63: Mob Speech and Expressions Example
Triggers[63] = {
    name = "Mob Speech and Expressions Example",
    attach_type = 0,
    trigger_type = 8,
    numeric_arg = 1,
    execute = function(self, actor, extra)
        -- By Rumble of The Builder Academy    tbamud.com 9091
        -- Arg list * means it will fire whenever someone talks.
        -- Check if it is a player character (not a mob).
        if actor_is_pc then
        coroutine.yield(1)
        -- Check the actors sex.
        if actor_sex == male then
        self:say("Good day sir, what would you like?")
        elseif actor_sex == female then
        coroutine.yield(1)
        self:say("Good day maam, what can I get you?")
        else
        self:say("What do you want?")
        end
        -- Check if the player level is less than 10 (9 and lower).
        if actor_level < 10 then
        self:say("you are a player below level 10.")
        else
        self:say("you are not below level 10.")
        end
        -- Check class not equal to warrior.
        if actor_class ~= warrior then
        self:say("you are not a warrior.")
        else
        self:say("you are a warrior.")
        end
        -- Check alignment between 350 and -350 (neutral).
        if actor_align <= 350 and actor_align >= -350 then
        self:say("you are neutral.")
        else
        self:say("you are not neutral.")
        end
        -- Check strength greater than or equal to 16 and less than or equal to 18. (16-18)
        -- If this was done without the equalities (=) it would check str 17 only.
        if actor_str >= 16 and actor_str <= 18 then
        self:say("your strength is 16-18.")
        else
        self:say("your strength is not 16-18.")
        end
        -- Check the speech to see if it is a substring of the word concatenated.
        -- concaten caten cat and any abbreviations.
        if concatenated /= speech then
        self:say("your speech was a substring of concatenated.")
        else
        self:say("your speech was not a substring of concatenated.")
        end
        -- Create a random number 1-10 and assign it to rnumber.
        -- TODO: set rnumber %random.10%
        self:say("your random number is: %rnumber%")
        -- increment it by 1.
        -- TODO eval: eval rnumber %rnumber% + 1
        self:say("your incremented random number is: %rnumber%")
        else
        self:say("you are not a player.")
        end
    end,
}

